CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

CREATE SCHEMA IF NOT EXISTS "Events";

CREATE TABLE "Building" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Description" text NULL,
    CONSTRAINT "PK_Building" PRIMARY KEY ("Id")
);

CREATE TABLE "EquipmentItem" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Description" text NULL,
    CONSTRAINT "PK_EquipmentItem" PRIMARY KEY ("Id")
);

CREATE TABLE "NewRoomInfo" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "RoomName" text NULL,
    "RoomType" integer NOT NULL,
    "RoomStatus" integer NOT NULL,
    CONSTRAINT "PK_NewRoomInfo" PRIMARY KEY ("Id")
);

CREATE TABLE "RoomDimension" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "X" double precision NOT NULL,
    "Y" double precision NOT NULL,
    "Width" double precision NOT NULL,
    "Height" double precision NOT NULL,
    CONSTRAINT "PK_RoomDimension" PRIMARY KEY ("Id")
);

CREATE TABLE "Shift" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Start" timestamp without time zone NOT NULL,
    "End" timestamp without time zone NOT NULL,
    "Name" text NULL,
    CONSTRAINT "PK_Shift" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."BackToMap" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "fromBouldingId" integer NOT NULL,
    CONSTRAINT "PK_BackToMap" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."BuildingSelection" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "buildingId" integer NOT NULL,
    CONSTRAINT "PK_BuildingSelection" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."EquipmentTransferEvent" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "SourceRoomId" integer NOT NULL,
    "DestinationRoomId" integer NOT NULL,
    "TransferDate" timestamp without time zone NOT NULL,
    "TransferDuration" integer NOT NULL,
    "EquipmentId" integer NOT NULL,
    "Quantity" integer NOT NULL,
    CONSTRAINT "PK_EquipmentTransferEvent" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."FloorChange" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "buildingId" integer NOT NULL,
    "fromFloor" integer NOT NULL,
    "toFloor" integer NOT NULL,
    CONSTRAINT "PK_FloorChange" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."MergeRenovationEvent" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "FirstOldRoomId" integer NOT NULL,
    "SecondOldRoomId" integer NOT NULL,
    "NewRoomName" text NULL,
    "Start" timestamp without time zone NOT NULL,
    "End" timestamp without time zone NOT NULL,
    CONSTRAINT "PK_MergeRenovationEvent" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."RoomSelection" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "RoomId" integer NOT NULL,
    CONSTRAINT "PK_RoomSelection" PRIMARY KEY ("Id")
);

CREATE TABLE "Events"."SplitRenovationEvent" (
    "Id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "TimeStamp" timestamp without time zone NOT NULL,
    "RoomId" integer NOT NULL,
    "FirstNewRoomName" text NULL,
    "SecondNewRoomName" text NULL,
    "Start" timestamp without time zone NOT NULL,
    "End" timestamp without time zone NOT NULL,
    "EquipmentDestination" text NULL,
    CONSTRAINT "PK_SplitRenovationEvent" PRIMARY KEY ("Id")
);

CREATE TABLE "MergeRenovation" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "FirstOldRoomId" integer NOT NULL,
    "SecondOldRoomId" integer NOT NULL,
    "NewRoomInfoId" integer NULL,
    "Start" timestamp without time zone NOT NULL,
    "End" timestamp without time zone NOT NULL,
    CONSTRAINT "PK_MergeRenovation" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_MergeRenovation_NewRoomInfo_NewRoomInfoId" FOREIGN KEY ("NewRoomInfoId") REFERENCES "NewRoomInfo" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "SplitRenovation" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "RoomId" integer NOT NULL,
    "FirstNewRoomInfoId" integer NULL,
    "SecondNewRoomInfoId" integer NULL,
    "Start" timestamp without time zone NOT NULL,
    "End" timestamp without time zone NOT NULL,
    "EquipmentDestination" text NULL,
    CONSTRAINT "PK_SplitRenovation" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_SplitRenovation_NewRoomInfo_FirstNewRoomInfoId" FOREIGN KEY ("FirstNewRoomInfoId") REFERENCES "NewRoomInfo" ("Id") ON DELETE RESTRICT,
    CONSTRAINT "FK_SplitRenovation_NewRoomInfo_SecondNewRoomInfoId" FOREIGN KEY ("SecondNewRoomInfoId") REFERENCES "NewRoomInfo" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "Room" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Status" integer NOT NULL,
    "Type" integer NOT NULL,
    "Floor" integer NOT NULL,
    "RoomDimensionsId" integer NOT NULL,
    "BuildingId" integer NOT NULL,
    CONSTRAINT "PK_Room" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Room_Building_BuildingId" FOREIGN KEY ("BuildingId") REFERENCES "Building" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_Room_RoomDimension_RoomDimensionsId" FOREIGN KEY ("RoomDimensionsId") REFERENCES "RoomDimension" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Doctor" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Name" text NULL,
    "Surname" text NULL,
    "DateOfBirth" timestamp without time zone NOT NULL,
    "Phone" text NULL,
    "Email" text NULL,
    "Gender" integer NOT NULL,
    "Specialization" integer NOT NULL,
    "RoomId" integer NULL,
    CONSTRAINT "PK_Doctor" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Doctor_Room_RoomId" FOREIGN KEY ("RoomId") REFERENCES "Room" ("Id") ON DELETE RESTRICT
);

CREATE TABLE "Equipment" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "RoomId" integer NOT NULL,
    "EquipmentItemId" integer NOT NULL,
    "Quantity" integer NOT NULL,
    "ReservedQuantity" integer NOT NULL,
    CONSTRAINT "PK_Equipment" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Equipment_EquipmentItem_EquipmentItemId" FOREIGN KEY ("EquipmentItemId") REFERENCES "EquipmentItem" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_Equipment_Room_RoomId" FOREIGN KEY ("RoomId") REFERENCES "Room" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Workday" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "DoctorId" integer NOT NULL,
    "ShiftId" integer NOT NULL,
    CONSTRAINT "PK_Workday" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Workday_Doctor_DoctorId" FOREIGN KEY ("DoctorId") REFERENCES "Doctor" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_Workday_Shift_ShiftId" FOREIGN KEY ("ShiftId") REFERENCES "Shift" ("Id") ON DELETE CASCADE
);

CREATE TABLE "EquipmentTransfer" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "SourceRoomId" integer NOT NULL,
    "DestinationRoomId" integer NOT NULL,
    "TransferDate" timestamp without time zone NOT NULL,
    "TransferDuration" integer NOT NULL,
    "EquipmentId" integer NOT NULL,
    "Quantity" integer NOT NULL,
    CONSTRAINT "PK_EquipmentTransfer" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_EquipmentTransfer_Room_DestinationRoomId" FOREIGN KEY ("DestinationRoomId") REFERENCES "Room" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_EquipmentTransfer_Equipment_EquipmentId" FOREIGN KEY ("EquipmentId") REFERENCES "Equipment" ("Id") ON DELETE CASCADE,
    CONSTRAINT "FK_EquipmentTransfer_Room_SourceRoomId" FOREIGN KEY ("SourceRoomId") REFERENCES "Room" ("Id") ON DELETE CASCADE
);

CREATE TABLE "Appointment" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "StartTime" timestamp without time zone NOT NULL,
    "EndTime" timestamp without time zone NOT NULL,
    "WorkdayId" integer NULL,
    CONSTRAINT "PK_Appointment" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_Appointment_Workday_WorkdayId" FOREIGN KEY ("WorkdayId") REFERENCES "Workday" ("Id") ON DELETE RESTRICT
);

